DROP TABLE IF EXISTS migration_doc_all_folders_and_files;
CREATE TABLE migration_doc_all_folders_and_files as
(
    with folders_and_files as
    (select uuid() newItemId, t.* from
        (select distinct id
        , SUBSTRING_INDEX(name, '/', -1) name
        , case when fileSortType = 30 then 'file' else case when fileSortType = 20 then 'document' else case when fileSortType = 10 then 'folder' else '??' end end end itemType
    from t_file_base
    where forEvent = 0) t)

    select faf1.newItemId preparisItemId, fb.id, fb.parent_id, faf2.newItemId preparisParentItemId, fb.name, faf1.itemType,
    ROW_NUMBER() over (partition by itemType order by id) rwo_num
    from t_file_base fb
    join folders_and_files faf1 on faf1.id = fb.id
    left join folders_and_files faf2 on faf2.id = fb.parent_id
);

DROP TABLE IF EXISTS migration_doc_all_folders;
CREATE TABLE migration_doc_all_folders as
(
    select * from migration_doc_all_folders_and_files where itemType = 'folder'
);

DROP TABLE IF EXISTS migration_doc_all_files;
CREATE TABLE migration_doc_all_files as
(
    select * from migration_doc_all_folders_and_files where itemType = 'file'
);

select count(*) foldersCount from migration_doc_all_folders;
