/*Delete records for ITDR (areaType 3)*/
delete from migration_custom_fields
where areaType = 3;

delete from migration_custom_fields_options
where areaType = 3;

/*Update row numbers*/
update migration_custom_fields mcf
join (
select ROW_NUMBER() OVER (ORDER BY id) row_num,
id
from migration_custom_fields
) t on t.id = mcf.id
set mcf.row_num = t.row_num;


update migration_custom_fields_options mcfo
join (
select ROW_NUMBER() OVER (ORDER BY id) row_num,
id
from migration_custom_fields_options
) t on t.id = mcfo.id
set mcfo.row_num = t.row_num;

select count(*) from migration_custom_fields_options;
